import React from 'react';
import { useNavigate } from 'react-router-dom';
import NavigationBarHome from '../components/NavigationBarHome';
import UserDashboard from '../components/UserDashboard';
import RecipeCard from '../components/RecipeCard';

import { APICall } from '../helperFunc';

export default function UserProfileScreen2 () {
  const navigate = useNavigate();

  const [tabValue, setTabValue] = React.useState("Saved")
  const [shownRecipes, setShownRecipes] = React.useState([])
  const [ratedRecipes, setRatedRecipes] = React.useState([])
  const [myRecipes, setMyRecipes] = React.useState([])

  let dummyRecipes = [
    {
      "recipe_id" : 1,
      "recipe_name": 'Boiled Egg',
      "recipe_desc": "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Perspiciatis amet odio dolorem nulla dolore hic enim blanditiis aperiam repellendus. Cum doloribus expedita ullam assumenda commodi molestias magnam est fugiat modi!",
      "recipe_time": 20,
      "is_liked": false,
      "recipe_ratings": 3.5,
      "recipe_tags": [{"tag_id" : 1, "tag_name" : "Australian"}],
      "recipe_image": "data:image/jpeg;base64,"
    },
    {
      "recipe_id" : 2,
      "recipe_name": 'Boiled Water',
      "recipe_desc": "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Perspiciatis amet odio dolorem nulla dolore hic enim blanditiis aperiam repellendus. Cum doloribus expedita ullam assumenda commodi molestias magnam est fugiat modi!",
      "recipe_time": 10,
      "is_liked": true,
      "recipe_ratings": 5,
      "recipe_tags": [{"tag_id" : 4, "tag_name" : "Breakfast"}],
      "recipe_image": "data:image/jpeg;base64,"
    }
  ];

  React.useEffect(() => {
    if (!localStorage.getItem('token')){
      alert("Please Log in Beforehand")
      navigate("/")
    }
  }, [])

  const fetchSaved = async() => {
    try {
      const headers = {
        'Content-Type': 'application/json',
        'token' : localStorage.getItem('token')
      };
      const temp_data = await APICall(null, '/dash/saved', 'GET', headers);
      let data = []
      for(let i = 0; i < temp_data.recipes.length; i++) {
        data.push({
          "recipe_id" : temp_data.recipes[i].recipe_id,
          "recipe_name": temp_data.recipes[i].title,
          "recipe_desc": temp_data.recipes[i].description,
          "recipe_time": temp_data.recipes[i].time_required,
          "is_liked": temp_data.recipes[i].saved,
          "recipe_ratings": temp_data.recipes[i].avg_rating,
          "recipe_tags": temp_data.recipes[i].tags,
          "recipe_image": temp_data.recipes[i].image
        })
      }
      setShownRecipes(data)
    } catch (err) {
      alert(err);
    }
  }

  const fetchMyRecipes = async() => {
    try {
      const headers = {
        'Content-Type': 'application/json',
        'token' : localStorage.getItem('token')
      };
      const temp_data = await APICall(null, '/dash/my_recipes', 'GET', headers);
      let data = []
      for(let i = 0; i < temp_data.recipes.length; i++) {
        data.push({
          "recipe_id" : temp_data.recipes[i].recipe_id,
          "recipe_name": temp_data.recipes[i].title,
          "recipe_desc": temp_data.recipes[i].description,
          "recipe_time": temp_data.recipes[i].time_required,
          "is_liked": temp_data.recipes[i].saved,
          "recipe_ratings": temp_data.recipes[i].avg_rating,
          "recipe_tags": temp_data.recipes[i].tags,
          "recipe_image": temp_data.recipes[i].image
        })
      }
      setShownRecipes(data)
    } catch (err) {
      alert(err);
    }
  }

  const fetchRated = async() => {
    try {
      const headers = {
        'Content-Type': 'application/json',
        'token' : localStorage.getItem('token')
      };
      const temp_data = await APICall(null, '/dash/rated', 'GET', headers);
      setRatedRecipes(temp_data)
    } catch (err) {
      alert(err);
    }
  }
  
  React.useEffect(() => {
    if(tabValue === 'Saved') {
      fetchSaved()
    } else if (tabValue === 'Rated') {
      fetchRated()
    } else if (tabValue === 'Own') {
      fetchMyRecipes()
    }
  }, [tabValue])

  const renderRecipesCard = () => {
    let content = []
    for (let i = 0; i < shownRecipes.length; i++) {
      content.push(
        <RecipeCard object={shownRecipes[i]} isEditable={false} isSaveable={(localStorage.getItem('token')) ? true : false} handleAfterLike={fetchSaved}/>
      )
    }
    return content
  }

  const renderMyRecipes = () => {
    let content = []
    for (let i = 0; i < myRecipes.length; i++) {
      content.push(
        <RecipeCard object={myRecipes[i]} isEditable={false} isSaveable={(localStorage.getItem('token')) ? true : false} handleAfterLike={fetchMyRecipes}/>
      )
    }
    return content
  }

  const renderRatedRecipes = () => {
    let data = []
    for (const [key, value] of Object.entries(ratedRecipes)) {
      let content = []
      for (let j = 0; j < value.length; j++) {
        content.push(
          <RecipeCard object={{
            "recipe_id" : value[j].recipe_id,
            "recipe_name": value[j].title,
            "recipe_desc": value[j].description,
            "recipe_time": value[j].time_required,
            "is_liked": value[j].saved,
            "recipe_ratings": value[j].avg_rating,
            "recipe_tags": value[j].tags,
            "recipe_image": value[j].image
          }} isEditable={false} isSaveable={(localStorage.getItem('token')) ? true : false} handleAfterLike={fetchRated}/>
        )
      }
      let wrapper = (
        <div style= {{
          position: 'relative',
          display: 'flex',
          flexDirection: 'column',
          width: '100%',
          marginBottom: '20px' 
        }}>
          <div style={{
            fontSize: '25px',
            fontWeight: 'bold'
          }}>
            {key}
          </div>
          <div style={{
            position: 'relative',
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-evenly',
            alignItems: 'center',
            flexWrap: 'wrap',
            alignContent: 'center',
            width: '100%'
          }}> 
            {content}
          </div>
        </div>
      )
      data.push(wrapper)
    }
    return (
      <div style={{
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
        width: '90%',
        marginTop: '20px',
        marginLeft: '20px',
      }}>
        {data}
      </div>
    )
  }
  
  return (
    <>
      <div style={{ 
        display:'flex',
        flexDirection:'column',
        height: '100%',
        alignItems:"center"
      }}>
        <NavigationBarHome style={{ alignSelf: 'start' }} isLogin={true} ></NavigationBarHome>
        <UserDashboard tabValue={tabValue} setTabValue={setTabValue}></UserDashboard>
        {(tabValue === 'Saved') && <div style={{
          position: 'relative',
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-evenly',
          flexWrap: 'wrap',
          alignContent: 'flex-start',
          marginTop: '20px',
          marginLeft: '20px',
          width: '90%'
        }}>
          {renderRecipesCard()}
        </div>}
        {(tabValue === "Rated") && renderRatedRecipes()}
        {(tabValue === "Own") && <div style={{
          position: 'relative',
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-evenly',
          flexWrap: 'wrap',
          alignContent: 'flex-start',
          marginTop: '20px',
          marginLeft: '20px',
          width: '90%'
        }}>
          {renderMyRecipes()}
        </div>}
      </div>
    </>
  )
}